mapper.update:
UPDATE a_Authority
auth_name = #{auth_name}, mod_id = #{mod_id}, auth_type = #{auth_type}, auth_description = #{auth_description}, auth_enable = #{auth_enable}
WHERE auth_id = #{auth_id}

mapper.select:
SELECT * FROM a_Authority

	AND auth_enable = #{auth_enable}
	AND mod_id = #{mod_id}
	AND auth_name LIKE '%${auth_name}%'
	AND auth_type = #{auth_type}

ORDER BY mod_id ASC, auth_name ASC
