mapper.update:
UPDATE b_Vault
alias = #{alias}, comment = #{comment}, card_bank = #{card_bank}, card_owner = #{card_owner}, payment_count = #{payment_count}, income_count = #{income_count}, last_modify_time = #{last_modify_time}, last_modify_person = #{last_modify_person}, enable = #{enable}
WHERE v_number = #{v_number}

mapper.select:
SELECT * FROM b_Vault


		v_number LIKE '%${v_number}%'


		AND type = #{type}


		AND alias LIKE '%${alias}%'


		AND card_bank LIKE '%${card_bank}%'


		AND card_owner LIKE '%{card_owner}%'



	ORDER BY create_time desc


	ORDER BY ${sort} 


	ORDER BY ${sort} desc


	LIMIT #{startIndex}, #{currentItemCount}

mapper.update:
UPDATE b_Vault
SET
enable = #{enable}
WHERE
v_number = #{v_number}
